<script>
import homeslide4bg1 from '../../assets/img/framework/homeslide4bg1.jpg'
import slide4mbbg from '../icons/slide4-mb-bg.vue'
export default {
  name: 'homeSlide4',
  components: {
    slide4mbbg
  },
  data() {
    return {
      homeslide4bg1: homeslide4bg1,
      isInViewport: false, // 盒子是否在视口中
      currentLine: 0, // 当前激活的行索引
      svgcolor: '#D6D6D6'
    }
  },

  mounted() {
    this.observeViewport()
    this.checkViewport()
    this.$refs.svg0.setAttribute('fill', this.svgcolor)
  },
  beforeUnmount() {
    if (this.observer) {
      this.observer.disconnect()
    }
  },
  methods: {
    observeViewport() {
      this.observer = new IntersectionObserver(
        ([entry]) => {
          this.isInViewport = entry.isIntersecting
          this.checkViewport()
        },
        { threshold: 0.1 }
      )
      this.observer.observe(this.$refs.slideContainer)
    },
    checkViewport() {
      if (this.isInViewport) {
        window.addEventListener('wheel', this.handleWheel)
      } else {
        window.removeEventListener('wheel', this.handleWheel)
      }
    },
    handleWheel(event) {
      const lines = this.$refs.slideContainer.querySelectorAll('.text-line')
      this.$refs.svg0.setAttribute('fill', this.svgcolor)

      if (event.deltaY < 0) {
        //向上滚
        this.currentLine = Math.max(this.currentLine - 1, 0)
      } else {
        this.currentLine = Math.min(this.currentLine + 1, lines.length)
      }
      lines.forEach((line, index) => {
        if (index === this.currentLine) return
        line.classList.remove('active')
      })

      if (this.currentLine === 4) {
        this.svgcolor = 'black'
        this.$refs.svg0.setAttribute('fill', this.svgcolor)
      } else {
        this.svgcolor = '#D6D6D6'
        this.$refs.svg0.setAttribute('fill', this.svgcolor)
      }

      if (this.currentLine <= 3) {
        lines[this.currentLine].classList.add('active')
      }
    }
  }
}
</script>
<style scoped>
