  font-size: 18px;
  line-height: 39.87px;
  color: #d6d6d6; /* 默认文本颜色为灰色 */
  transition: color 0.0000001s ease; /* 颜色变化的过渡效果 */
}

.slide4-mbbg {
  display: none;
}

.slide4-mbicon {
  display: none;
}

.text-line.active {
  color: black; /* 激活颜色设置为黑色 */
}

.slide4 {
  height: 420px;
  position: relative; /* 设置容器为相对定位 */
  width: 100%; /* 根据需要设置容器的宽度 */

  z-index: -1; /* 确保图片在内容之下 */
  background-image: url(../../assets/img/framework/homeslide4bg1.jpg);
  background-attachment: fixed;
  background-repeat: no-repeat;
  background-position: center;
  background-size: 1420px 100%;
  background-color: #fafafa;
}

.slide4-content-text {
  position: absolute; /* 绝对定位，相对于最近的定位祖先元素（这里是.slide-container） */
  top: 36%; /* 将文字垂直居中，可以根据需要调整 */
  left: 50%; /* 将文字水平居中，可以根据需要调整 */
  transform: translate(-50%, -50%); /* 用于微调文本位置，使其完美居中 */
  color: black; /* 设置文字颜色，确保在图片上可见 */
  text-align: center; /* 文本居中显示 */
  z-index: 1; /* 确保文字位于图片上方 */
  /* 其他样式，如字体大小、行高等 */
}

@media screen and (max-width: 510px) {
  .slide4 {
    margin-top: 58px;
